(function() {
	   function write(path, scriptList) {
		   for (var i = 0; i < scriptList.length; i++) {
			document.write('<script type="text/javascript" src="' + path + scriptList[i] + ' "></script>');
		   }
	   }
	   // scripts list
	   var SCRIPT_PATH = 'js/lib/';
	   var SCRIPT_LIST = [
	         'zepto.js', 
	         'event.js'
	         ];
	   write(SCRIPT_PATH, SCRIPT_LIST);
})();
